import React, { useState } from 'react';
import { StyleSheet, View, Text, TouchableOpacity, TouchableHighlight, TextInput } from 'react-native';

function VaccineEdit(props) {
  const { title: initialTitle, description: initialDescription, id } = props.route.params.item;

  const [title, setTitle] = useState(initialTitle);
  const [description, setDescription] = useState(initialDescription);

  const handleSave = () => {
    const updatedItem = { title, description };
    // Add your save functionality here
  
    const UpdateData = () => {
      fetch(`http://192.168.43.11:8000/api/articles/${id}/`, {
        method: 'PUT',
        headers: {
          'Content-Type':'application/json'
        },
        body: JSON.stringify({title, description})
      })
      .then(resp => resp.json())
      .then(data => {
        // console.log(data)
        props.navigation.navigate('Home', {data:data})
      })
    }

    UpdateData(); // call the UpdateData function here
  }

  return (
    <View style={styles.container}>
      <TextInput
        placeholder="Title"
        value={title}
        mode="outlined"
        onChangeText={text => setTitle(text)}
        style={styles.titleInput}
      />

      <TextInput
        style={styles.descriptionInput}
        placeholder="Description"
        value={description}
        mode="outlined"
        multiline
        numberOfLines={10}
        onChangeText={text => setDescription(text)}
      />

      <TouchableHighlight style={styles.button} onPress={handleSave}>
        <Text style={styles.buttonText}>Update</Text>
      </TouchableHighlight>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#fff',
    padding: 20,
  },
  titleInput: {
    width: '100%',
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    padding: 10,
    marginBottom: 30,
  },
  descriptionInput: {
    width: '100%',
    height: 200, // set a larger height value for the description input
    borderColor: 'gray',
    borderWidth: 1,
    padding: 10,
  },

  button: {
    backgroundColor: '#2196F3',
    padding: 10,
    borderRadius: 5,
    marginTop: 20, // add some margin to the top of the button
  },
  buttonText: {
    color: '#fff',
    textAlign: 'center',
    fontWeight: 'bold',
    fontSize: 18,
  },
});

export default VaccineEdit;
