import React from 'react'
import { StyleSheet, View, Text, TouchableOpacity, ScrollView, Alert } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';

function IssueDetails({ route, navigation }) {
  const { item } = route.params;

  const handleEdit = () => {
    // Add your edit functionality here
    // navigation.navigate('Edit')
    navigation.navigate('EditIssue', {item});
    // navigation.navigate('VaccineEdit', { item: item, id: item.id });

  };

  const handleDelete = () => {
    fetch(`http://192.168.43.11:8000/api/IssueVaccines/${item.id}/`, {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json'
      },
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const contentLength = response.headers.get('Content-Length');
        if (contentLength === '0') {
          return null; // return null if response is empty
        }
        return response.json();
      })
      .then(data => {
        console.log(data);
        navigation.navigate('IssuedVaccines');
      })
      .catch(error => Alert.alert('Error', error.toString()));
  };
  
  
  return (
    <ScrollView style={styles.container}>
      <View style={styles.viewStyle}>
        <Text style={{fontSize:25}}>{item.title}</Text>
        {/* <Text style={{fontSize:20, marginTop:10}}>{item.description}</Text> */}


        <Text style={{ fontSize: 20, marginTop: 10, color: 'red' }}>Vaccine: {item.vaccine_name}</Text>

        {/* <Text style={{fontSize:20, marginTop:10, color: 'red'}}>{item.vaccine_name}</Text> */}
        <Text style={{fontSize:20, marginTop:10}}>District: {item.district}</Text>
        <Text style={{fontSize:20, marginTop:10}}>Date: {item.Date}</Text>
        <Text style={{fontSize:20, marginTop:10}}>Health Facility: {item.health_facility}</Text>
        <Text style={{fontSize:20, marginTop:10}}>Issued quantity: {item.issue_quantity}</Text>
        <Text style={{fontSize:20, marginTop:10}}>Issued by: {item.issue_by}</Text>




        <View style={styles.buttonContainer}>
          <TouchableOpacity style={styles.editButton} onPress={handleEdit}>
            <Icon name="edit" size={25} color="white" />
            <Text style={styles.buttonText}>Edit</Text>
          </TouchableOpacity>

          <TouchableOpacity style={styles.deleteButton} onPress={handleDelete}>
            <Icon name="trash" size={25} color="white" />
            <Text style={styles.buttonText}>Delete</Text>
          </TouchableOpacity>
        </View>

      </View>
    </ScrollView>
  );
}

const styles=StyleSheet.create({
  container: {
    flex: 1,
  },
  viewStyle: {
    padding:10,
    margin:10
  },
  buttonContainer: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: 20,
  },
  editButton: {
    backgroundColor: '#2E64FE',
    flexDirection: 'row',
    alignItems: 'center',
    borderRadius: 10,
    padding: 10,
  },
  deleteButton: {
    backgroundColor: '#FF0000',
    flexDirection: 'row',
    alignItems: 'center',
    borderRadius: 10,
    padding: 10,
    marginLeft: 10,
  },
  buttonText: {
    color: 'white',
    fontSize: 20,
    marginLeft: 10,
  },
});

export default IssueDetails;
