import React, { useState, useEffect } from 'react';
import { StyleSheet, View, Text, FlatList, TouchableOpacity, Alert } from 'react-native';

export default function Home(props) {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  const loadData = () => {
    // fetch('http://192.168.43.8:8000/api/articles/')
    fetch('http://192.168.43.11:8000/api/receive-from-other-hf/')
      .then((resp) => resp.json())
      .then((data) => {
        setData(data);
        setLoading(false);
      })
      .catch((error) => {
        Alert.alert('Error', error.toString());
      });
  };

  useEffect(() => {
    loadData();
  }, []);

  const clickedItem = (item) => {
    props.navigation.navigate('Details', { item });
  };

  const renderItem = ({ item }) => (
    <TouchableOpacity onPress={() => clickedItem(item)}>
      <View style={[styles.card, item.id === 1 && { marginTop: 40 }]} key={item.id}>
        <Text style={{ fontSize: 18, fontWeight: 'bold' }}>{item.vaccine_name}</Text>
      </View>
    </TouchableOpacity>
  );

  return (
    <View style={{ flex: 1 }}>
      {/* <TouchableOpacity
        style={styles.fabMinus}
        onPress={() => props.navigation.navigate('Create')}
        
      >
        <Text style={{ color: 'white', fontSize: 20 }}>-</Text>
      </TouchableOpacity> */}

      <FlatList
        data={data}
        renderItem={renderItem}
        keyExtractor={(item) => `${item.id}`}
        onRefresh={loadData}
        refreshing={loading}
      />

      <TouchableOpacity
        style={styles.fabPlus}
        onPress={() => props.navigation.navigate('Create')}
      >
        <Text style={{ color: 'white', fontSize: 20 }}>+</Text>
      </TouchableOpacity>


      <TouchableOpacity
        style={styles.fabMinus}
        onPress={() => props.navigation.navigate('IssueVaccine')}
        
      >
        <Text style={{ color: 'white', fontSize: 20 }}>-</Text>
      </TouchableOpacity>



    </View>
  );
}

const styles = StyleSheet.create({
  card: {
    backgroundColor: 'white',
    borderRadius: 5,
    padding: 10,
    margin: 10,
    shadowColor: 'black',
    shadowOpacity: 0.2,
    shadowOffset: {
      width: 0,
      height: 1,
    },
    elevation: 1,
  },
  fabPlus: {
    position: 'absolute',
    margin: 16,
    right: 0,
    bottom: 0,
    backgroundColor: 'blue',
    borderRadius: 50,
    width: 50,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: 'black',
    shadowOpacity: 0.2,
    shadowOffset: {
      width: 0,
      height: 1,
    },
    elevation: 2,
  },
  fabMinus: {
    position: 'absolute',
    margin: 16,
    left: 0,
    bottom: 0,
    backgroundColor: 'red',
    borderRadius: 50,
    width: 50,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: 'black',
    shadowOpacity: 0.2,
    shadowOffset: {
      width: 0,
      height: 1,
    },
    elevation: 2,
  },
});
