import React, { useState, useEffect} from 'react';
import { StyleSheet, View, Text, FlatList, TouchableOpacity, Alert } from 'react-native';


export default function Home(props) {
  // const [count, setCount] = useState(0);
  const [data, setData] = useState([])
  const [loading, setLoading] = useState(true)
  const loadData=() =>{

    fetch('http://192.168.43.11:8000/api/articles/',{
      method:'GET'
    })
    // .then(resp => resp.json)
    .then(resp => resp.json())

    .then(data=>{
      setData(data)
      setLoading(false)
    })
    .catch(error => Alert. alert('error', error))

  }
  useEffect(() =>{
    loadData();
  }, [])


  // ===================================
  // const clickedItem=(data)=> {
  //   props.navigation.navigate('Details')
  // }
// ]]]]]]]]]]]]]]]]]]]
  const clickedItem = (item) => {
    props.navigation.navigate('Details', { item });
  };
  
// ========================================= 
  // const renderItem = ({ item }) => (
  //   <View style={[styles.card, item.id === 1 && { marginTop: 40 }]} key={item.id} onPress={()=>clickedItem(item)}>
  //   {/* <View style={styles.card} key={item.id}> */}
  //     <Text style={{ fontSize: 18, fontWeight: 'bold' }}>{item.title}</Text>
  //     {/* <Text style={{ fontSize: 14 }}>{item.description}</Text>  */}
  //   </View>
  // );

  // ==================================

  const renderItem = ({ item }) => (
    <TouchableOpacity onPress={() => clickedItem(item)}>
      <View style={[styles.card, item.id === 1 && { marginTop: 40 }]} key={item.id}>
        <Text style={{ fontSize: 18, fontWeight: 'bold' }}>{item.title}</Text>
      </View>
    </TouchableOpacity>
  );
  
  // ====================================

  return (
    <View style={{flex:1}}>
     {/* <Text style={{ fontSize: 24, margin: 10 }}>Count: {count}</Text>  */}
      <FlatList
        // data={mydata}
        data={data}
        renderItem={renderItem}
        // keyExtractor={item => item.id.toString()}
        onRefresh = {()=> loadData()}
        refreshing ={loading}
        keyExtractor={item => `${item.id}`}
      />
      
      <TouchableOpacity
        style={styles.fab}
        // onPress={() => setCount(count + 1)}
        // onPress={() => props.navigation.navigate(Create)}
        onPress={() => props.navigation.navigate('Create')}

      >
        <Text style={{ color: 'white', fontSize: 20 }}>+</Text>
      </TouchableOpacity>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'pink',
  },
  card: {
    backgroundColor: 'white',
    borderRadius: 5,
    padding: 10,
    margin: 10,
    shadowColor: 'black',
    shadowOpacity: 0.2,
    shadowOffset: {
      width: 0,
      height: 1,
    },
    elevation: 1,
  },
  textStyle: {
    fontSize: 25,
    color: 'red'
  },
  fab: {
    position: 'absolute',
    margin: 16,
    right: 0,
    bottom: 0,
    backgroundColor: 'blue',
    borderRadius: 50,
    width: 50,
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
    shadowColor: 'black',
    shadowOpacity: 0.2,
    shadowOffset: {
      width: 0,
      height: 1,
    },
    elevation: 2,
  },

});
